from csv import reader as cread
from csv import writer as cwrite
from json import dump as jdump
from json import load as jload
import PySimpleGUI as sg

ICON = "".encode()


ROWS = []
FIELDS = []
IDS = {}
CARDS = {}
HELP = ""
ERR = ""


cont = True
try:
    with open("books.csv", "r", encoding="utf-8") as f:
        _raw = cread(f)
        FIELDS = next(_raw)
        for _row in _raw:
            ROWS.append(_row)
        f.close()
except FileNotFoundError:
    cont = False
    ERR += "books.csv, "

if cont == True:
    try:
        with open("knihy.csv", "x", encoding="utf-8") as f:
            _wrt = cwrite(f)
            _wrt.writerow(FIELDS)
            _wrt.writerows(ROWS)
            f.close()
    except FileExistsError:
        ERR += "knihy.csv, "


cont = True
try:
    with open("ids.json", "r", encoding="utf-8") as f:
        IDS = jload(f)
        f.close()
except FileNotFoundError:
    cont = False
    ERR += "ids.json, "

if cont == True:
    try:
        with open("kody.json", "x", encoding="utf-8") as f:
            jdump(IDS, f)
            f.close()
    except FileExistsError:
        ERR += "kody.json, "


cont = True
try:
    with open("cards.json", "r", encoding="utf-8") as f:
        CARDS = jload(f)
        f.close()
except FileNotFoundError:
    cont = False
    ERR += "cards.json, "

if cont == True:
    try:
        with open("karty.json", "x", encoding="utf-8") as f:
            jdump(CARDS, f)
            f.close()
    except FileExistsError:
        ERR += "karty.json, "


cont = True
try:
    with open("help.txt", "r", encoding="utf-8") as f:
        HELP = f.read()
        f.close()
except FileNotFoundError:
    cont = False
    ERR += "help.txt, "

if cont == True:
    try:
        with open("pomoc.txt", "x", encoding="utf-8") as f:
            f.write(HELP)
            f.close()
    except FileExistsError:
        ERR += "pomoc.txt, "


sg.theme("DarkRed2")

if ERR != "":
    ERR = ERR[:-2]
    sg.PopupError("Následující soubory nebylo možné načíst či uložit:",
                  ERR,
                  "Ujistěte se, že se v dané složce nacházejí "
                  + "pouze zdrojové (anglické) soubory.", icon=ICON)
else:
    sg.PopupOK("Všechny soubory byly v pořádku převedeny do "
               + "nového formátu můžete spustit novou verzi.", icon=ICON)
